/* Header
 * ========================================================================== */

.banner-legal {
  @include background-base($steel);
  padding: $padding-one-third;
  text-align: center;
}

.site-header {
  width: 100%;
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: $padding-one-half $padding;
}
.logo {
  padding: $padding-one-half;
  max-width: 60%;
}
.nav-toggle {
  position: relative;
  display: block;
  width: 40px;
  height: 30px;
  z-index: 12;
  span {
    position: absolute;
    top: 50%;
    left: 50%;
    display: block;
    width: 100%;
    height: 2px;
    background-color: $black;
    border-radius: 2px;
    transform: translate(-50%, -50%);
    transition: background 0s 0s;
    &::before,
    &::after {
      @include pseudo();
      left: 0;
      width: 100%;
      height: 2px;
      background-color: $black;
      border-radius: 2px;
      transition-delay: 0.1s 0s;
      transition-duration: 0.2s;
    }
    &::before {
      top: -8px;
      transition-property: top, transform;
    }

    &::after {
      bottom: -8px;
      transition-property: bottom, transform;
    }
  }
}
.nav-active {
  span {
    background: none;

    &::before,
    &::after {
      background-color: $ivory;
      transition-delay: 0s 0.2s;
      transition-duration: 0.2s;
    }
    &::before {
      top: 0;
      transform: rotate(45deg);
    }
    &::after {
      bottom: 0;
      transform: rotate(-45deg);
    }
  }
}
.primary-nav {
  position: absolute;
  width: 80%;
  height: 100vh;
  top: 0;
  display: none;
  right: 0;
  background-color: $steel;
  transition: all 0.7s ease-in;
  z-index: 10;
  &.nav-active {
    display: block;
  }
  .nav-header {
    display: none;
    width: 100%;
    height: 20vh;
    justify-content: flex-end;
    align-items: center;
    padding: 0 $padding;
    &.nav-active {
      display: flex;
    }
  }
  .nav-linklist.nav-active {
    display: grid;
    grid-template-columns: 1fr;
    grid-template-rows: repeat(4, 100px);
    align-items: center;
    height: 100%;
    width: 100%;
    padding: 0 2em;
    .nav-item {
      padding-bottom: 10px;
      border-bottom: 1.5px solid $ivory;
      a {
        @include font-stack(Avenir-Medium, 1.2em, normal);
        color: $ivory;
      }
    }
  }
}

.nav-item a {
  &:hover {
    cursor: pointer;
  }
}

@include for-size(tablet, up) {
  .site-header {
    padding: $padding-one-half;
    flex-wrap: wrap;
  }
  .nav-toggle {
    display: none;
  }
  .primary-nav {
    position: relative;
    right: auto;
    height: auto;
    display: flex;
    width: fit-content;

    .nav-linklist {
      display: inherit;
      justify-content: flex-end;
      align-items: center;
      background-color: white;

      .nav-item a {
        padding: $padding-one-third;
        margin: 0 $padding-one-third;
        color: $black;
        @include font-stack(Avenir-Medium, $baseFontSize, normal);
      }
    }
  }
}

sources.src/scss/_homepage.scss:
/* Top Banner
 * ========================================================================== */
.section-wrapper-1 {
  @include gradient(top right, $navy, $navy-light 250%);
}
.main-banner {
  display: flex;
  flex-flow: column wrap;
  height: 100%;
  min-height: 40vh;
  overflow: hidden;
  position: relative;
  padding: $padding * 6 $padding;
  align-items: center;
  justify-content: center;
  .hero-text {
    text-align: center;
    margin-bottom: 0;
  }
  .subtitle {
    @include font-stack($base-font, 1.05em, 100);
    letter-spacing: 1.5px;
    font-style: oblique;
    padding: 20px 0;
    text-align: center;
  }
}
.button-container {
  display: inherit;
  width: 100%;
  flex-direction: column;
  align-items: center;
  justify-content: center;
}

@include for-size(phone-lg, up) {
  .button-container {
    flex-direction: row;
    margin: $padding * 3.5 0 $padding * 2 0;
  }
}
/* Resources Section
 * ========================================================================== */
.section-wrapper-2 {
  position: relative;
  @include background("/src/images/resources-bg.jpg", cover, 70% bottom);
  &::after {
    @include pseudo();
    top: 0;
    right: 0;
    left: 0;
    bottom: 0;
    @include gradient(right, $white-light, $clear 130%);
  }
}

.resources {
  margin: 0 auto;
  height: 100%;
  min-height: 60vh;
  overflow: hidden;
  padding: $padding * 7 $padding $padding * 3;
  z-index: 4;

  & .title-text {
    overflow-wrap: break-word;
    max-width: 800px;
    width: 100%;
    margin-bottom: 0;
  }
}

.banner-description {
  padding: $padding 0;
  font-style: oblique;
}

.card-columns {
  margin: 4em auto 2em;
  width: fit-content;
  display: grid;
  justify-content: center;
  align-content: center;
  grid-gap: 1em;
  grid-template-columns: 1fr;
  grid-template-rows: repeat(5, auto);
  grid-auto-rows: auto;

  .resource-link {
    text-align: center;
    margin: 1em auto 0;
  }
}

@include for-size(tablet, up) {
  .card-columns {
    margin: 5em auto 2em;
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    grid-template-rows: repeat(2, 1fr);
    width: fit-content;
  }
}

@include for-size(tablet-lg, up) {
  .card-columns {
    margin: 5em 0 2em;
  }
}

/* Benefits Section
 * ========================================================================== */
.section-wrapper-3 {
  @include background-base($white);
}

.benefits {
  margin: 5em auto;
  padding: 2em 1em;
  display: flex;
  flex-flow: row wrap;
  justify-content: center;

  .right {
    height: 100%;
    display: none;
  }
  .left {
    .h3 {
      @include font-stack(Avenir-Medium, $h3-size, norml);
      margin-bottom: 0.5em;
      position: relative;

      &::after {
        @include pseudo();
        background-color: $green;
        height: 3px;
        bottom: -2px;
        left: 0;
        width: 40px;
      }
    }

    .benefit-description {
      margin: 0.5em 0 2em 1.25em;
    }
  }
}
.benefit-container {
  padding: 0.5em 1em;
}

@include for-size(tablet, up) {
  .benefits {
    margin: 5em auto;
    display: flex;
    flex-flow: row wrap;

    .right {
      display: block;
      flex-basis: 50%;
      padding-right: 2em;
      height: 100%;
    }
    .left {
      flex-basis: 50%;

      .benefit-description {
        margin: 0.5em 0 2em 1.25em;
      }
    }
  }
}

/* Block Section
 * ========================================================================== */

.section-wrapper-3 {
  @include background-base($white);
}
.blocks {
  display: grid;
  justify-content: center;
  align-content: center;
  grid-gap: 0.5em;
  grid-template-columns: repeat(2, 1fr);
  grid-template-rows: repeat(2, auto);
  grid-auto-rows: auto;
}

.block-card {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 200px;
  box-shadow: 0px 0px 10px -1px rgba(#000, 0.1);
  position: relative;
  border-radius: 3px;
  color: $white;

  p,
  a {
    text-align: center;
    z-index: 999;
  }

  &:nth-child(1) {
    grid-row: 1;
    grid-column: 1 / span 2;
    @include background("/src/images/modu-va-loan.jpg");
  }
  &:nth-child(2) {
    grid-row: 2;
    grid-column: 1 / span 2;
    @include background("/src/images/modu-resources.jpg");
  }
  &:nth-child(3) {
    grid-row: 3;
    grid-column: 1 / span 2;
    @include background("/src/images/modu-find-lender.jpg");
  }

  &::after {
    @include pseudo();
    border-radius: 1px;
    height: 100%;
    top: 0;
    right: 0;
    left: 0;
    bottom: 0;
    background-color: rgba(0, 0, 0, 0.5);
  }

  @include for-size(tablet, up) {
    min-height: 300px;

    &:nth-child(1) {
      grid-row: 1;
      grid-column: 1;
    }
    &:nth-child(2) {
      grid-row: 2;
      grid-column: 1;
    }
    &:nth-child(3) {
      grid-row: 1 / span 2;
      grid-column: 2;
    }
  }
}
@include for-size(tablet, up) {
  .blocks {
    grid-gap: 2em;
  }
}
/* Search Section
 * ========================================================================== */
.section-wrapper-5 {
  position: relative;
  &::after {
    @include pseudo();
    top: 0;
    right: 0;
    left: 0;
    bottom: 0;
    z-index: -1;
    @include background("/src/images/modu-va-loan.jpg", cover, center center);
    background-color: rgba(255, 255, 255, 0.85);
    background-blend-mode: lighten;
  }
}

.search {
  width: 100%;

  .field {
    position: relative;
    display: grid;
    grid-template-columns: 1fr;
    grid-template-rows: auto;
    grid-row-gap: 1em;

    .h3 {
      font-size: 1.7em;
      text-align: center;
    }
  }

  .input-search {
    border: 1px solid $steel;
    height: 45px;
    border-radius: 3px;
    box-shadow: 0 1px 0 #bfbebf;
    padding: 0.15em 2em;
    width: 100%;
  }
}

@include for-size(tablet, up) {
  .search .field {
    grid-template-columns: 1fr 1fr;
    .h3 {
      font-size: 1.7em;
      text-align: left;
      overflow-wrap: break-word;
      max-width: 400px;
    }
    .input-search {
      height: 65px;
    }
  }
}

